'use client';

import { useState } from 'react';
import {AdminMessage as AdminMessageType, Prisma, RoleStatus, UserRole} from "@prisma/client";
import {acceptMessageAction} from "@/lib/actions/adminMessage";
import {useRouter} from "next/navigation";


const messageWithUser = Prisma.validator<Prisma.AdminMessageDefaultArgs>()({
  include: {
    user: true,
  },
})

type MessageWithUser = Prisma.AdminMessageGetPayload<typeof messageWithUser>


export async function MessagesList({messages}: {
  messages: MessageWithUser[];
}) {

  return (
    <div>
    <ul>
      {messages.map(message => (
          <AdminMessage key={message.id} message={message}/>
      ))}
    </ul>
    </div>
  );
}

export  function AdminMessage({message}: { message: MessageWithUser }){
  const router = useRouter()

  const acceptCustomer = async () => {
    try{
      const res = await acceptMessageAction(message.id, message.user.id)
      if (res){
        router.refresh()
      }
    }catch (e) {
      console.log(e)
    }
  }
  return(
      <li className="pb-3 sm:pb-4">
        <div className="flex items-center space-x-4 rtl:space-x-reverse flex-col md:flex-row">
          <div className="flex-shrink-0">
            <img className="w-8 h-8 rounded-full" src="data:image/png;base64," alt=" Neil image"/>
          </div>
          <div className=" flex-1 ">
            <p className=" text-sm font-medium text-gray-900 truncate dark:text-white">
              {message.user.name}
            </p>
            <p className=" text-sm text-gray-500 truncate dark:text-gray-400">
              {message.user.email}
            </p>
            <p className=" text-sm text-gray-500 truncate dark:text-gray-400">
              {message.message}
            </p>
          </div>
          <button onClick={acceptCustomer}
                  className={`text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm w-full sm:w-auto px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800 ${(message.user.role != UserRole.USER && message.user.roleStatus == RoleStatus.APPROVED)  && 'opacity-50 pointer-events-none'}`}>Submit
          </button>
        </div>
      </li>
  )
}